    public Manager() {

    }

    /**
     * Funcio que comprova si el password compleix els requeriments
     * @param password es el password a comprovar
     */

    public boolean checkPassword(String password) {
        patterPass = Pattern.compile(regexPass);
        boolean ok = patterPass.matcher(password).matches();
        return ok;
    }

    public boolean checkEmail(String email) {
        patternMail = Pattern.compile(regexMail);
        boolean ok = patternMail.matcher(email).matches();
        return ok;
    }

    //ActualUser -> treballem amb el usuari que ha fet el login
    public void setActualUser(User user) {
        this.actualUser = user;
    }

    public User getActualUser() {
        return actualUser;
    }

    public ArrayList<Company> getCompanies() {
        return companies;
    }

    public void setCompanies(ArrayList<Company> companies) {
        this.companies = companies;
    }

    public void setActualUserCompanyShares(UserCompany actualCompanyShares) {
        this.actualCompanyUserShares = actualCompanyUserShares;
    }

    public UserCompany getActualUserCompanyShares() {
        return actualCompanyUserShares;
    }

    public Company getActualCompany() {
        return actualCompany;
    }

    public void setActualCompany(Company actualCompany) {
        this.actualCompany = actualCompany;
    }

    public void updateCompanies(ArrayList<Company> companiesList) {
        this.companies = companiesList;
    }

    public Company getCompanieFromId(int id) {
        for (Company c : companies) {
            if (c.getCompanyId() == id) {
                return c;
            }
        }
        return null;
    }

    public void updateActualComapny() {
        for (Company c : companies) {
            if (c.getCompanyId() == actualCompany.getCompanyId()) {
                actualCompany = c;
            }
        }
    }
